function randomInteger(e,n){return Math.floor(Math.random()*(n-e)+e)}function updateHouseholdIncome(){let e=document.getElementById("income").value;document.getElementById("income-value").innerHTML=e,setProportunityValue(),setBankValue()}function updateSavings(){let e=document.getElementById("savings").value;document.getElementById("savings-value").innerHTML=e,setProportunityValue(),setBankValue()}function setProportunityValue(){document.getElementById("proportunity-value").innerHTML=randomInteger(200,999)}function setBankValue(){let e=parseInt(document.getElementById("proportunity-value").textContent);document.getElementById("bank-value").innerHTML=randomInteger(0,e)}